mapper.select:
select *
from t_user
where user_email = #{ user_email }

update t_user
set user_name = #{ user_name }
where user_no = #{ user_no }

mapper.update:
update t_user
set	login_ok = #{ login_ok }
where user_email = #{ user_email }

update t_user
set user_admin = #{ user_admin }
where user_no = #{ user_no }

mapper.select:

			SELECT *
			FROM ( SELECT ROWNUM RNUM, N.*
              FROM (select *
					       from t_user) N)
			WHERE RNUM >= #{ startRow } AND RNUM <= #{ endRow }
			order by user_no
