mapper.select:
select *
from t_user
where user_email = #{ user_email }

update t_user
set user_name = #{ user_name }
where user_no = #{ user_no }

select *
from t_trash
where user_no = #{ user_no }

mapper.update:
update t_user
set user_admin = #{ user_admin }
where user_no = #{ user_no }

mapper.select:
select *
from t_trash
where to_char(trash_date, 'YYMMDD') = to_char(sysdate, 'YYMMDD');
