mapper.select:
select * from User 
where userid = #{ userid } and userpwd = #{ userpwd } 
and login_ok = 'Y'

select * from User

	where age >= #{ value }


	where age between #{ value } and #{ value } + 9

select count(userid) 
from User 
where userid = #{ uid }

mapper.update:
update User 
set userpwd = #{ userpwd }, 			
	age = #{ age }, 
	phone = #{ phone }, email = #{ email }, 
	hobby = #{ hobby }, etc = #{ etc } 
where userid = #{ userid }

update User 
set login_ok = #{ login_ok } 
where userid = #{ userid }
